mapper.select:
select id, askid, createid,content,billurl,companyName, linkMan, telephone, email
from t_p_offer_price
where id = #{id,jdbcType=BIGINT}

select t.id,t.offerTime dateTime,concat(p.name,c.name) deliveryAddress,if(t.type = 1,'公开询价','定向询价') typeName,t.title,t.createid from (select op.id,op.createid,op.askid,date_format(op.offerTime,'%Y-%m-%d') offerTime,ap.provinceCode,ap.cityCode,ap.type,ap.title from t_p_offer_price op left join t_p_ask_price ap on ap.id = op.askid
and ap.title like concat('%',#{title},'%')
and op.createid = #{createid}
and date_format(op.offerTime,'%Y-%m-%d') >= date_format(#{startDate},'%Y-%m-%d') and date_format(op.offerTime,'%Y-%m-%d') <= date_format(#{endDate},'%Y-%m-%d')
) t left join t_dictionary_province p on t.provinceCode = p.code left join t_dictionary_city c on t.cityCode = c.code
