mapper.select:
select * from t_sys_advertising adv
where adv.advAttrId in (
select a.id from t_sys_adv_attr a
where a.chanType = #{chanType}
and a.page = #{page}
and a.advArea = #{advArea}
) order by adv.sort asc

select * from t_sys_advertising adv
where adv.advAttrId in (
select a.id from t_sys_adv_attr a
where a.chanType = #{chanType}
and a.page = #{page}
and a.advArea = #{advArea}
) order by adv.sort asc
limit 3

select * from t_sys_advertising adv
where
adv.advAttrId in (
select a.id from t_sys_adv_attr a
where a.chanType = #{chanType}
and a.page = #{page}
and a.advArea = #{advArea}
)
order by adv.createTime DESC
limit 3

mapper.insert:
insert into t_sys_advertising (id, title, advAttrId, 
  advType, linkUrl, imgUrl, 
  createTime, operatorId, connectedId,sort
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{advAttrId,jdbcType=CHAR}, 
  #{advType,jdbcType=VARCHAR}, #{linkUrl,jdbcType=VARCHAR}, #{imgUrl,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{operatorId,jdbcType=INTEGER}, #{connectedId,jdbcType=VARCHAR},#{sort,jdbcType=CHAR}
  )
