mapper.update:
update t_sys_messageLog

  
    status = #{status},
  

where messageID = #{messageID} and recID = #{recID}

update t_sys_messageLog
set recID = #{recID,jdbcType=BIGINT},
  messageID = #{messageID,jdbcType=BIGINT},
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

mapper.select:
    select count(mg.recID) msgCount
	 from t_sys_messageLog mg ,t_sys_messageText mt
	 where mt.id = mg.messageID
       
         and mg.recID = #{recID}
       
       
         and mt.type = #{type}
       
       
         and mg.status = #{status}

select msgCount1+msgCount2 as count from
(select count(mt.id)msgCount1 from t_sys_messageText mt where mt.sendID is null and mt.id not in (select ml.messageID from
t_sys_messageLog ml where ml.recID = #{recID}))T1,
(select count(mg.recID) msgCount2
from t_sys_messageLog mg ,t_sys_messageText mt
where mt.id = mg.messageID

  and mg.recID = #{recID}


  and mt.type = #{type}


  and mg.status = #{status}

)T2

SELECT mt.id,mt.title FROM t_sys_messageText mt
LEFT JOIN t_sys_messageLog ml on mt.id = ml.messageID
WHERE ml.recID = #{recID} and ml.status != 3 order by ml.status,mt.sendTime desc limit 0,#{count}
