mapper.insert:
insert into t_sys_messageText (id, sendID, messageText, 
  type, sendTime, typeID
  )
values (#{id,jdbcType=BIGINT}, #{sendID,jdbcType=BIGINT}, #{messageText,jdbcType=VARCHAR}, 
  #{type,jdbcType=INTEGER}, #{sendTime,jdbcType=TIMESTAMP}, #{typeID,jdbcType=BIGINT}
  )

mapper.select:
select ml.id,mt.messageText,mt.`type`,date_format(mt.sendTime,'%Y-%m-%d %H:%i:%s') sendTime, ml.`status`,case when mt.type = 1 then '产品消息' when mt.type = 2 then '活动消息' end as typeName from t_sys_messageLog ml left join t_sys_messageText mt on mt.id = ml.messageID where ml.`status`!=3 and ml.recID = #{recID}
and mt.type = #{type}
order by mt.sendTime desc
