mapper.select:
select a.id,date_format(a.publishTime,'%Y-%m-%d') publishTime,date_format(a.updateTime,'%Y-%m-%d') updateTime, a.title,a.systemType,a.useArea,a.companyName,a.linkMan,a.telephone,a.email,a.province,a.city,a.area,a.address,a.cooperationType,a.status, a.introduce,p.name as provinceName,c.name as cityName,ar.name as areaName,con.name as systemName, co.name as useAreaName,m.personRealName as createName from t_e_achievement a LEFT JOIN t_dictionary_province p on a.province = p.code LEFT JOIN t_dictionary_city c on a.city = c.code LEFT JOIN t_dictionary_area ar on a.area = ar.code LEFT JOIN t_dictionary_constant con on con.code = a.systemType and con.type = 19 LEFT JOIN t_dictionary_constant co on co.code = a.useArea and co.type = 18 LEFT JOIN t_m_member m on a.createId = m.id where a.is_deleted = 0
and a.title like CONCAT('%',#{title},'%')
and a.status = #{status}
and a.createId = #{createId}
and a.systemType = #{systemType}
and a.useArea = #{useArea}
and a.status = 1
order by a.updateTime desc

mapper.update:
update t_zhb_viewGoods
set status = 1
where id = #{id}

mapper.select:
select c.id,c.title,date_format(v.viewTime,'%Y-%m-%d') viewTime, v.id as recordId,co.name as systemName,con.name as areaName from t_zhb_viewGoods v left join t_e_achievement c on v.goodsId = c.id left join t_dictionary_constant co on c.systemType = co.code and co.`type` = 19 left join t_dictionary_constant con on c.useArea = con.code and con.`type` = 18 where v.type = 'CKZJJSCG' and v.`status` = 0
and v.companyId = #{companyId}
and v.viewerId = #{viewerId}
order by v.viewTime desc

select a.id,date_format(a.publishTime,'%Y-%m-%d') publishTime,date_format(a.updateTime,'%Y-%m-%d') updateTime, a.title,a.systemType,a.useArea,a.companyName,a.linkMan,a.telephone,a.email,a.province,a.city,a.area,a.address,a.cooperationType,a.status, a.introduce,p.name as provinceName,c.name as cityName,ar.name as areaName,con.name as systemName, co.name as useAreaName,m.personRealName as createName from t_e_achievement a LEFT JOIN t_dictionary_province p on a.province = p.code LEFT JOIN t_dictionary_city c on a.city = c.code LEFT JOIN t_dictionary_area ar on a.area = ar.code LEFT JOIN t_dictionary_constant con on con.code = a.systemType and con.type = 19 LEFT JOIN t_dictionary_constant co on co.code = a.useArea and co.type = 18 LEFT JOIN t_m_member m on a.createId = m.id where a.is_deleted = 0
and a.status = #{status}
and a.createId = #{createId}
GROUP by a.updateTime order by a.updateTime DESC

select c.id,c.title,date_format(v.viewTime,'%Y-%m-%d') viewTime,v.type, v.id as recordId,co.name as systemName,con.name as areaName,v.goodsId from t_zhb_viewGoods v left join t_e_achievement c on v.goodsId = c.id left join t_dictionary_constant co on c.systemType = co.code and co.`type` = 19 left join t_dictionary_constant con on c.useArea = con.code and con.`type` = 18 where v.type = 'CKZJJSCG' and v.`status` = 0
and v.companyId = #{companyId}
and v.viewerId = #{viewerId}
and a.status = #{status}
GROUP by v.viewTime order by v.viewTime DESC
