mapper.select:
select e.id,e.createid,e.title,c.name as type,e.subType,e.views,date_format(e.updateTime,'%Y-%m-%d') updateTime, e.status,date_format(e.publishTime,'%Y-%m-%d') publishTime,e.startDate,e.endDate,e.imgUrl,e.address,e.introduce,e.company, case when e.createrType = 1 then (select u.username from t_oms_user u where u.id = e.createid) when e.createrType = 2 then (select m.enterpriseName from t_m_member m where m.id = e.createid) end as createName, ci.name as cityName,p.name as provinceName,a.name as areaName from t_c_exhibition e left join t_dictionary_constant c on e.`type` = c.code and c.`type` = 17 left join t_dictionary_city ci on ci.code = e.city left join t_dictionary_province p on p.code = e.province left join t_dictionary_area a on a.code = e.area where e.is_deleted = 0
and e.title like CONCAT('%',#{title},'%')
and e.createid = #{createId}
and e.status = #{status}
and e.type = #{type}
and e.subType = #{subType}
and e.province = #{province}
and e.status = 1
and e.type in (1,2,3)
order by e.updateTime desc

select a.connectedId as id,a.imgUrl,a.title
from t_sys_advertising a
left join t_sys_adv_attr at on at.id = a.advAttrId
where at.chanType = #{chanType}
and at.page = #{page}
and at.advArea = #{advArea}
and at.advType = #{advType}
