mapper.select:
select

from yw_goods
where id = #{id,jdbcType=INTEGER}

SELECT a.*,b.img_url AS imgurl
FROM (SELECT t1.id, t1.name, t1.price, t1.origprice
FROM yw_goods t1

    WHERE t1.id NOT IN

        #{item}


) a
LEFT JOIN (SELECT * FROM yw_goods_img WHERE type = '2') b
ON a.id = b.goodsid
LIMIT #{defaultcount,jdbcType=INTEGER}

SELECT
s.id,s.name,a.typename,d.brandname,s.origprice,s.price,s.stock,s.sales,s.ishomepage,s.recommenddate,s.isdel,s.ishot,s.isessence,COUNT(c.goodsid)
AS collect,s.onlinetime,s.protype,s.brandid from yw_goods s
LEFT JOIN yw_brand d ON d.brandid=s.brandid LEFT JOIN yw_collection c ON c.goodsid=s.id LEFT JOIN yw_category a
ON a.protype=s.protype
WHERE 1=1

    AND s.name LIKE concat('%',#{name,jdbcType=VARCHAR},'%')


    AND s.attribute=#{producttags,jdbcType=VARCHAR}


    AND s.brandid=#{brand,jdbcType=INTEGER}


    AND s.protype=#{protype,jdbcType=INTEGER}


    AND s.isdel=#{isdel,jdbcType=INTEGER}


    AND s.price BETWEEN #{minorigprice,jdbcType=DOUBLE} AND #{maxorigprice,jdbcType=DOUBLE}


    AND s.origprice BETWEEN #{minorigprice,jdbcType=DOUBLE} AND #{maxorigprice,jdbcType=DOUBLE}


    AND s.stock BETWEEN #{minorigprice,jdbcType=INTEGER} AND #{maxorigprice,jdbcType=INTEGER}


    AND s.sales BETWEEN #{minorigprice,jdbcType=INTEGER} AND #{maxorigprice,jdbcType=INTEGER}


    AND s.collect BETWEEN #{minorigprice,jdbcType=INTEGER} AND #{maxorigprice,jdbcType=INTEGER}


    AND s.onlinetime BETWEEN #{mintime,jdbcType=DATE} AND #{maxtime,jdbcType=DATE}

GROUP BY s.id

    ORDER BY s.sales DESC


    ORDER BY s.stock DESC


    ORDER BY s.origprice DESC


    ORDER BY s.collect DESC

update.foreach:
UPDATE yw_goods
SET stock = stock - #{item.sum,jdbcType=INTEGER}
WHERE id = #{item.goodsid,jdbcType=INTEGER}

mapper.select:
SELECT g.*, i.img_url from yw_goods g LEFT JOIN yw_collection c ON g.id = c.goodsid LEFT JOIN yw_goods_img i ON g.id = i.goodsid where c.type = 1 and i.type = 2
AND c.userid = #{userid}
order by c.collect_time DESC

SELECT g.id, b.brandname, g.name,g.protype,g.isdel,g.origprice,g.price,g.sales,g.stock,g.attribute FROM yw_goods g LEFT JOIN yw_brand b ON g.brandid=b.brandid WHERE 1
AND b.brandname=#{brandname,jdbcType=VARCHAR}
AND g.name LIKE concat('%',#{name,jdbcType=VARCHAR},'%')
AND g.protype=#{protype,jdbcType=INTEGER}

mapper.update:
update yw_goods_img


        img_url = #{imgurl,jdbcType=VARCHAR}


WHERE goodsid=#{id,jdbcType=INTEGER} AND type =2

mapper.select:
SELECT g.id,u.nickname,u.level,g.goodpoint,g.logisticpoint,g.servicepoint,g.content,(SELECT COUNT(1) FROM yw_goods_assessment_img i WHERE i.assessmentid=g.id) AS number,g.createtime,g.goodid,g.pid FROM yw_goods_assessment g LEFT JOIN yw_user u on u.id=g.userid WHERE g.userid<>-1
AND g.goodid=#{goodsid,jdbcType=INTEGER}
AND u.nickname LIKE concat('%',#{nickname,jdbcType=VARCHAR},'%')
AND g.content LIKE concat('%',#{content,jdbcType=VARCHAR},'%')
AND g.createtime BETWEEN #{mintime,jdbcType=DATE} AND #{maxtime,jdbcType=DATE}
ORDER BY g.createtime DESC

mapper.update:
update yw_goods_img


        img_url = #{imgurl,jdbcType=VARCHAR}


WHERE goodsid= #{goodsid,jdbcType=INTEGER} AND type=3

update yw_goods_img


        img_url = #{imgurl,jdbcType=VARCHAR}


WHERE goodsid= #{goodsid,jdbcType=INTEGER} AND type=1

mapper.select:
SELECT c.id,c.comboid,c.comboname,c.combodiscountprice,c.intime,
(SELECT COUNT(1) FROM yw_sub_order o WHERE o.combotype=c.comboid ) AS sales
FROM yw_combo c WHERE 1=1

    AND c.comboname LIKE concat('%',#{comboname,jdbcType=VARCHAR},'%')


    AND c.combodiscountprice BETWEEN #{minrex,jdbcType=DOUBLE} AND #{maxrex,jdbcType=DOUBLE}


    AND (SELECT COUNT(1) FROM yw_sub_order o WHERE o.combotype=c.comboid ) BETWEEN #{minrex,jdbcType=INTEGER}
    AND #{maxrex,jdbcType=INTEGER}


    AND c.comboid=#{comboid,jdbcType=INTEGER}


    AND c.intime BETWEEN #{mintime,jdbcType=DATE} AND #{maxtime,jdbcType=DATE}


    AND (SELECT SUM(g.price) FROM yw_goods g WHERE c.comboid=g.id) BETWEEN #{minrex,jdbcType=DOUBLE} AND
    #{maxrex,jdbcType=DOUBLE}


    AND name LIKE concat('%',#{name,jdbcType=VARCHAR},'%')


    ORDER BY (SELECT SUM(g.price) FROM yw_goods g WHERE c.comboid=g.id) DESC


    ORDER BY (SELECT COUNT(1) FROM yw_sub_order o WHERE o.combotype=c.comboid ) DESC


    ORDER BY c.intime DESC

SELECT c.id,c.comboid,c.comboname,c.combodiscountprice,c.intime,
(SELECT COUNT(1) FROM yw_sub_order o WHERE o.combotype=c.comboid ) AS sales,c.imgurl
FROM yw_combo c WHERE c.comboid=#{comboid,jdbcType=INTEGER}

         SELECT g.goodsid,d.price,d.`name` ,d.origprice,d.stock,d.sales,d.attribute,d.id ,d.isdel,c.typename,b.brandname  FROM yw_goods_combo g
LEFT JOIN yw_goods d ON d.id=g.goodsid  LEFT JOIN yw_category
c ON c.protype=d.protype LEFT JOIN yw_brand b ON b.brandid=d.brandid WHERE g.comboid=#{comboid,jdbcType=INTEGER}

mapper.update:
update yw_combo


        comboname = #{comboname,jdbcType=VARCHAR}


        combodiscountprice = #{combodiscountprice,jdbcType=INTEGER}


        imgurl = #{combodiscountprice,jdbcType=VARCHAR}


WHERE comboid= #{comboid,jdbcType=INTEGER}
