mapper.select:
select

from yw_goods
where id = #{id,jdbcType=INTEGER}

SELECT a.*,b.img_url AS imgurl
FROM (SELECT t1.id, t1.name, t1.price, t1.origprice
FROM yw_goods t1

    WHERE t1.id NOT IN

        #{item}


) a
LEFT JOIN (SELECT * FROM yw_goods_img WHERE type = '2') b
ON a.id = b.goodsid
LIMIT #{defaultcount,jdbcType=INTEGER}

SELECT
s.id,s.name,s.protype,d.brandname,s.origprice,s.price,s.stock,s.sales,s.ishomepage,s.recommenddate,s.isdel,s.ishot,s.isessence,COUNT(c.goodsid)
AS collect from yw_goods s
LEFT JOIN yw_brand d ON d.brandid=s.brandid LEFT JOIN yw_collection c ON c.goodsid=s.id
WHERE 1=1

    AND s.name LIKE concat('%',#{name,jdbcType=VARCHAR},'%')


    AND s.attribute=#{producttags,jdbcType=INTEGER}


    AND s.brandname=#{brand,jdbcType=VARCHAR}


    AND s.protype=#{protype,jdbcType=INTEGER}


    AND s.isdel=#{isdel,jdbcType=INTEGER}


    AND s.price BETWEEN #{minprice,jdbcType=DOUBLE} AND #{maxprice,jdbcType=DOUBLE}


    AND s.origprice BETWEEN #{minorigprice,jdbcType=DOUBLE} AND #{maxorigprice,jdbcType=DOUBLE}


    AND s.stock BETWEEN #{minstock,jdbcType=INTEGER} AND #{maxstock,jdbcType=INTEGER}


    AND s.sales BETWEEN #{minsales,jdbcType=INTEGER} AND #{maxsales,jdbcType=INTEGER}


    AND s.collect BETWEEN #{mincollect,jdbcType=INTEGER} AND #{maxcollect,jdbcType=INTEGER}


    ORDER BY s.sales DESC


    ORDER BY s.stock DESC


    ORDER BY s.origprice DESC


    ORDER BY s.collect DESC

GROUP BY s.id

SELECT g.* from yw_goods g LEFT JOIN yw_collection c ON g.id = c.goodsid where c.type = 1
AND c.userid = #{userid}
order by c.collect_time DESC

SELECT g.id, b.brandname, g.name,g.protype,g.isdel,g.origprice,g.price,g.sales,g.stock,g.attribute FROM yw_goods g LEFT JOIN yw_brand b ON g.brandid=b.brandid WHERE 1
AND b.brandname=#{brandname,jdbcType=VARCHAR}
AND g.name LIKE concat('%',#{name,jdbcType=VARCHAR},'%')
AND g.protype=#{protype,jdbcType=INTEGER}

mapper.update:
update yw_goods


        name = #{name,jdbcType=VARCHAR}




        protype = #{protype,jdbcType=INTEGER}




        price = #{price,jdbcType=DOUBLE}




        origprice = #{origprice,jdbcType=DOUBLE}




        stock = #{stock,jdbcType=INTEGER}




        isdel = #{isdel,jdbcType=INTEGER}




        brandid = #{brandid,jdbcType=INTEGER}




        recommenddate = #{recommenddate,jdbcType=DATE}


where id=#{id,jdbcType=INTEGER}

update yw_goods_img


        img_url = #{imgurl,jdbcType=MultipartHttpServletRequest}


WHERE goodsid=(SELECT id FROM yw_goods WHERE id=#{id,jdbcType=INTEGER} )
