mapper.select:
select

from t_m_member WHERE status != 0 and status != 2

    AND mobile = #{mobile}


    AND email = #{email}

select count(1) from t_m_member WHERE status != 0 and status != 2

    AND mobile = #{mobile}


    AND email = #{email}

mapper.update:
update t_m_member

    
        status = #{status},
    

where email = #{email} and id = #{id}

mapper.select:
SELECT a.id,CASE WHEN a.mobile != '' OR a.mobile != NULL THEN a.mobile WHEN a.email!= '' OR a.email != NULL THEN a.email END AS account, a.password,a.status,a.identify,a.workType role,IF(b.status=1,'1','0') isexpert,a.enterpriseLinkman,a.fixedTelephone,a.email, CASE WHEN a.workType = 100 AND a.identify !=2 THEN a.id WHEN a.workType !=100 AND a.identify !=2 THEN a.enterpriseEmployeeParentId WHEN a.identify = 2 THEN a.id END AS companyId ,a.registerTime ,a.workType,a.headShot,a.nickname,a.enterpriseName AS companyName ,vip.vip_level,pvip.vip_level parentVipLevel FROM t_m_member a LEFT OUTER JOIN t_e_expert b ON a.id=b.createId AND b.status=1 AND b.is_deleted=0 LEFT JOIN t_vip_member_info vip ON a.id = vip.member_id LEFT JOIN t_vip_member_info pvip ON a.enterpriseEmployeeParentId = pvip.member_id WHERE a.status != 0 AND a.status != 2
AND a.mobile = #{mobile}
AND a.email = #{email}
AND a.id = #{id}
