mapper.select:
select id, title,views,publisher_id,status, (case when status=0 then '未发布' when status=1 then '已发布' end ) as status_text, (case when type=2 then '专题' when type=3 then '筑慧访谈' when type=4 then '曝光台'when type=5 then '工程商新闻'when type=6 then '深度观察'when type=7 then '活动' when type=1 and sub_type = 1 then '行业资讯--网络及硬件'when type=1 and sub_type = 2 then '行业资讯--安全防范'when type=1 and sub_type = 3 then '行业资讯--楼宇自动化'when type=1 and sub_type = 4 then '行业资讯--数据中心'when type=1 and sub_type = 5 then '行业资讯--智能家居'when type=1 and sub_type = 6 then '行业资讯--影音视频'when type=1 and sub_type = 7 then '行业资讯--应用系统'when type=1 and sub_type = 8 then '行业资讯--智能照明'when type=1 and sub_type = 9 then '行业资讯--行业软件' end) as show_type_name, date_format(publish_time,'%Y-%m-%d %H:%i:%s') as publish_time_str FROM t_n_news WHERE 1=1
and title like CONCAT('%',#{title},'%')
and sub_type = #{subType,jdbcType=INTEGER}
and type = #{type,jdbcType=INTEGER}
and status = #{status,jdbcType=INTEGER}
order by add_time desc

select id, title,views,source,photo, date_format(publish_time,'%Y-%m-%d %H:%i:%s') as publish_time_str FROM t_n_news where 1=1
and sub_type = #{subType,jdbcType=INTEGER}
and type = #{type,jdbcType=INTEGER}
order by views, publish_time desc
