mapper.select:
select

,

from t_oms_channel_news

  
    and channelid = #{channelid}
  
  
    and status = #{status}
  
  
    and sort = #{sort}
  
  
    and createid = #{createid}
  

order by createTime desc

  limit #{count}

select id,title,date_format(createTime,'%Y-%m-%d %H:%i:%s') createTime from t_oms_channel_news

  
    and channelid = #{channelid}
  
  
    and status = #{status}
  
  
    and sort = #{sort}
  
  
    and createid = #{createid}
  

order by createTime desc

select id,title,description,imgUrl,views from t_oms_channel_news

  
    and channelid = #{channelid}
  
  
    and status = #{status}
  
  
    and sort = #{sort}
  

order by views desc
limit #{count}

select a.id,a.title,
CONCAT(b.name,'>', (select w.name from t_oms_channel w where w.sort=a.sort and w.parentId=a.channelid) ) name
,a.views, (case when a.status=0 then '未审核' when a.status=1 then '已审核' when a.status='2' then '已拒绝' else '回收站' end ) statusText,a.createid, m.username publisher, date_format(a.createTime,'%Y-%m-%d %H:%i:%s') createTime from t_oms_channel_news a left join t_oms_channel b on a.channelid=b.id left join t_oms_user m on m.id=a.createid
a.status!=3
and a.channelid!='11'
and a.title like CONCAT('%',#{title},'%')
and a.status = #{status}
and a.channelid = #{channelid}
and a.sort = #{sort}
order by a.createTime desc

select a.id,a.title, date_format(a.createTime,'%Y-%m-%d %H:%i:%s') createTime, a.imgurl, a.description from t_oms_channel_news a
a.status!=3
and a.status = #{status}
and a.channelid = #{channelid}
and a.sort = #{sort}
order by a.views desc
order by a.createTime desc

select t.id, t.title,ur.username ,date_format(t.createTime,'%Y-%m-%d %H:%i:%s') createTime,t.imgurl,t.content,t.description from t_oms_channel_news t
left join t_oms_user ur on ur.id = t.createid

  
    and channelid = #{channelid}
  
  
    and status = #{status}
  
  
    and sort = #{sort}
  
  
    and createid = #{createid}
  

order by createTime desc

select t.id, t.title,ur.username ,date_format(t.createTime,'%Y-%m-%d %H:%i:%s') createTime,t.imgurl,t.content,t.description from t_oms_channel_news t left join t_oms_user ur on ur.id = t.createid
and t.id = #{id}
order by createTime desc

select a.connectedId as id,a.imgUrl,a.title
from t_sys_advertising a
left join t_sys_adv_attr at on at.id = a.advAttrId
where at.chanType = #{chanType}
and at.page = #{page}
and at.advArea = #{advArea}
and at.advType = #{advType}
order by a.sort   asc

select id,title,date_format(createTime,'%Y-%m-%d %H:%i:%s') createTime,description  from t_oms_channel_news

  
    and channelid = #{channelid}
  
  
    and sort = #{sort}
  
  
    and status = #{status}
  

order by createTime desc

  limit 0,#{count}

select id,title,views from t_oms_channel_news
where channelid = #{type} and status =1  order by views desc

  limit 0,#{count}

select id,title,date_format(createTime,'%Y-%m-%d') createTime  from t_oms_channel_news
where channelid = #{type} and status = 1 order by createTime desc

  limit 0,#{count}

select id,title,  date_format(createTime,'%Y-%m-%d %H:%i:%s') createTime from  t_oms_channel_news
where status = 1 and channelid = #{type} and sort = #{sort} order by   createTime  desc

  limit 0,#{count}
