mapper.select:
select

  ,

from t_oms_channel_news


    and channelid = #{channelid}


    and status = #{status}


    and sort = #{sort}


    and createid = #{createid}


order by createTime desc

  limit #{count}

select id,title,date_format(createTime,'%Y-%m-%d %H:%i:%s') createTime from t_oms_channel_news


    and channelid = #{channelid}


    and status = #{status}


    and sort = #{sort}


    and createid = #{createid}


order by createTime desc

select id,title,description,imgUrl,views from t_oms_channel_news


    and channelid = #{channelid}


    and status = #{status}


    and sort = #{sort}


order by views desc
limit #{count}

select a.id,a.title,
CONCAT(b.name,'>', (select w.name from t_oms_channel w where w.sort=a.sort and w.parentId=a.channelid) ) name
,a.views, (case when a.status=0 then '未审核' when a.status=1 then '已审核' when a.status='2' then '已拒绝' else '回收站' end ) statusText,a.createid, (case when m.personRealName is null then m.enterpriseName else m.personRealName end) publisher,date_format(createTime,'%Y-%m-%d %H:%i:%s') createTime from t_oms_channel_news a left join t_oms_channel b on a.channelid=b.id left join t_m_member m on m.id=a.createid
and a.title like CONCAT('%',#{title},'%')
and a.status = #{status}
and a.channelid = #{channelid}
and a.sort = #{sort}
order by a.createTime desc
