mapper.select:
Select

from t_o_sms where orderNo in

  #{item}

and o.orderNo = og.orderNo
and o.status = #{status}
and og.templateCode = #{templateCode}

mapper.insert:
insert into t_o_sms (orderNo, mobile, content, 
  status, sendTime, updateTime
  )
values (#{orderNo,jdbcType=VARCHAR}, #{mobile,jdbcType=VARCHAR}, #{templateCode,jdbcType=VARCHAR},#{content,jdbcType=VARCHAR},
  #{status,jdbcType=CHAR}, #{sendTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
