mapper.update:
UPDATE yw_post
SET zansum = zansum+1
WHERE id=#{id,jdbcType=INTEGER}

UPDATE yw_post
SET collectsum = collectsum - 1
WHERE id=#{postid,jdbcType=INTEGER}

UPDATE yw_post
SET forwardsum = forwardsum + 1
WHERE id=#{postid,jdbcType=INTEGER}

mapper.select:
SELECT p.zansum
FROM yw_post p
WHERE id=#{id,jdbcType=INTEGER}

SELECT p.*,(SELECT u.nickname FROM yw_user u WHERE u.id=p.userid) as nickname,
(SELECT COUNT(1) FROM yw_post_share sh WHERE sh.postid=p.id) as share,
(SELECT COALESCE(SUM(r.points),0) FROM yw_post_rewarded r WHERE r.postid=p.id) AS rewarded,
(SELECT COALESCE(SUM(ac.num),0) FROM yw_post_accusation ac WHERE ac.postid =p.id) AS accusation,
(SELECT name FROM yw_circle cir WHERE cir.id=p.circleid AND cir.isdel=0 AND cir.status=1) AS circlename,
(SELECT category FROM yw_circle cir WHERE cir.id=p.circleid AND cir.isdel=0 AND cir.status=1) AS category
FROM yw_post AS p
WHERE p.isdel <> 1 AND p.isactive=0
AND p.circleid in (SELECT circleid FROM yw_circle_manager WHERE userid=(SELECT id FROM yw_user WHERE phone
=(SELECT phone FROM yw_admin_user WHERE id=#{loginid,jdbcType=INTEGER})))
AND p.isdel <> 1

    AND u.id=#{uid,jdbcType=INTEGER}


    AND p.isactive=0


    AND p.isessence=1


    AND p.title LIKE concat('%',#{title,jdbcType=VARCHAR},'%')


    AND p.userid=#{userid,jdbcType=INTEGER}


    AND p.postcontent LIKE concat('%',#{postcontent,jdbcType=LONGVARCHAR},'%')


    AND p.circleid=#{circleid,jdbcType=INTEGER}


    AND p.intime BETWEEN #{begintime,jdbcType=DATE} AND #{endtime,jdbcType=DATE}


    AND p.essencedate=#{essencedate,jdbcType=DATE}

OR p.circleid IN (SELECT id FROM yw_circle WHERE phone=(SELECT phone FROM yw_admin_user WHERE
id=#{loginid,jdbcType=INTEGER})) AND isdel =0

    AND u.id=#{uid,jdbcType=INTEGER}


    AND p.isactive=0


    AND p.isessence=1


    AND p.title LIKE concat('%',#{title,jdbcType=VARCHAR},'%')


    AND p.userid=#{userid,jdbcType=INTEGER}


    AND p.postcontent LIKE concat('%',#{postcontent,jdbcType=VARCHAR},'%')


    AND p.circleid=#{circleid,jdbcType=INTEGER}


    AND p.intime BETWEEN #{begintime,jdbcType=DATE} AND #{endtime,jdbcType=DATE}


    AND p.essencedate=#{essencedate,jdbcType=DATE}


    ORDER BY p.intime DESC


    ORDER BY p.totalpoint DESC

SELECT p.*,(SELECT u.nickname FROM yw_user u WHERE u.id=p.userid) as nickname, (SELECT COUNT(1) FROM yw_post_share sh WHERE sh.postid=p.id) as share, (SELECT COALESCE(SUM(r.points),0) FROM yw_post_rewarded r WHERE r.postid=p.id) AS rewarded, (SELECT COALESCE(SUM(ac.num),0) FROM yw_post_accusation ac WHERE ac.postid =p.id) AS accusation, (SELECT name FROM yw_circle cir WHERE cir.id=p.circleid AND cir.isdel=0 AND cir.status=1) AS circlename, (SELECT category FROM yw_circle cir WHERE cir.id=p.circleid AND cir.isdel=0 AND cir.status=1) AS category FROM yw_post AS p WHERE p.isdel=0 AND p.isactive=0 AND p.userid=#{userid,jdbcType=INTEGER}
AND p.isessence=1
ORDER BY p.intime DESC

SELECT p.id,p.circleid,p.title,p.subtitle,p.postcontent,p.zansum,p.commentsum, p.forwardsum,p.collectsum,p.isactive,p.partsum_enddays,p.type,p.ishot, p.isessence,p.orderid,p.coverimg,p.hotimgurl,p.intime,p.totalpoint,p.isdel, p.isessencepool,p.activetype,p.activefee,p.iscontribute,p.essencedate,p.userid, p.oprtime,p.heat_value AS heatvalue,p.city,p.ishot_order AS ishotorder, (SELECT COUNT(1) FROM yw_post WHERE activeid = p.id) AS contribute FROM yw_post p WHERE p.isdel=0 AND p.isactive=1
AND p.userid=#{loginid,jdbcType=INTEGER}
ORDER BY p.intime DESC

mapper.update:
UPDATE yw_post
SET isdel=1
WHERE id=#{postid,jdbcType=INTEGER}

mapper.select:
SELECT p.*,u.nickname, (SELECT COUNT(1) FROM yw_post_share sh WHERE sh.postid=p.id) as share, (SELECT COALESCE(SUM(r.points),0) FROM yw_post_rewarded r WHERE r.postid=p.id) AS rewarded, (SELECT COALESCE(SUM(ac.num),0) FROM yw_post_accusation ac WHERE ac.postid =p.id) AS accusation, (SELECT name FROM yw_circle cir WHERE cir.id=p.circleid) AS circlename, (SELECT category FROM yw_circle cir WHERE cir.id=p.circleid) AS category FROM yw_post AS p LEFT JOIN yw_user u ON u.id=p.userid WHERE p.isdel=0 AND p.isactive=0
AND u.id=#{uid,jdbcType=INTEGER}
AND p.isactive=0
AND p.circleid=#{circleid,jdbcType=INTEGER}
AND p.isessence=1
AND p.title LIKE concat('%',#{title,jdbcType=VARCHAR},'%')
AND p.userid=#{userid,jdbcType=INTEGER}
AND p.postcontent LIKE concat('%',#{postcontent,jdbcType=LONGVARCHAR},'%')
AND p.intime BETWEEN #{begintime,jdbcType=DATE} AND #{endtime,jdbcType=DATE}
AND p.essencedate=#{essencedate,jdbcType=DATE}
ORDER BY intime DESC
ORDER BY totalpoint DESC

SELECT p.*, ap.begintime, ap.endtime FROM yw_post p LEFT JOIN yw_collection c on p.id = c.postid left join yw_active_period ap on ap.postid = p.id where c.type = 0 and p.isdel = 0
AND c.userid = #{userid}
ORDER BY c.collect_time DESC

