mapper.select:
SELECT p.*,(SELECT u.nickname FROM yw_user u WHERE u.id=p.userid) as nickname, (SELECT COUNT(1) FROM yw_post_share sh WHERE sh.postid=p.id) as share, (SELECT COALESCE(SUM(r.points),0) FROM yw_post_rewarded r WHERE r.postid=p.id) AS rewarded, (SELECT COALESCE(SUM(ac.num),0) FROM yw_post_accusation ac WHERE ac.postid =p.id) AS accusation, (SELECT name FROM yw_circle cir WHERE cir.id=p.circleid AND cir.isdel=0 AND cir.status=1) AS circlename, (SELECT category FROM yw_circle cir WHERE cir.id=p.circleid AND cir.isdel=0 AND cir.status=1) AS category FROM yw_post AS p WHERE p.isdel=0 AND p.isactive=0 AND p.isessencepool=1
AND userid=#{userid,jdbcType=INTEGER}
AND p.circleid=#{circleid,jdbcType=INTEGER}
AND p.title LIKE concat('%',#{title,jdbcType=VARCHAR},'%')
AND p.postcontent LIKE concat('%',#{postcontent,jdbcType=LONGVARCHAR},'%')
AND p.intime BETWEEN #{begintime,jdbcType=DATE} AND #{endtime,jdbcType=DATE}
AND p.essencedate=#{essencedate,jdbcType=DATE}
ORDER BY p.intime DESC
ORDER BY p.intime ASC

