mapper.select:
SELECT l.*, (SELECT u.nickname FROM yw_user u WHERE u.id = l.userid) AS userName FROM yw_post_label l WHERE isdel = 0
AND l.name LIKE concat('%',#{name,jdbcType=VARCHAR},'%')
AND l.type = #{type,jdbcType=INTEGER}
AND l.userid = #{userid,jdbcType=INTEGER}
ORDER BY heat_value DESC

mapper.update:
UPDATE yw_post_label
SET isdel = 1
WHERE id = #{id,jdbcType=INTEGER}
