mapper.select:
SELECT * FROM yw_robot_operation_job
where postid = #{postid} AND type = #{type}
ORDER by batch DESC limit 1

SELECT * FROM yw_robot_operation_job
where userid = #{userid} AND type = #{type}
ORDER by batch DESC limit 1

SELECT u.nickname as followedUser, p.title, u2.nickname as author, j.* FROM yw_robot_operation_job j LEFT JOIN yw_user u on j.userid = u.id LEFT JOIN yw_post p on j.postid = p.id LEFT JOIN yw_user u2 on u2.id = p.userid
and j.type = #{type}
ORDER BY j.intime DESC
