mapper.insert:
INSERT INTO yw_submission (
  title, videourl, userid, email, comment, status, intime
)
VALUES (#{title,jdbcType=VARCHAR}, #{videourl,jdbcType=VARCHAR},
  #{userid,jdbcType=INTEGER}, #{email,jdbcType=VARCHAR}, #{comment,jdbcType=VARCHAR},
  #{status,jdbcType=INTEGER}, #{intime,jdbcType=TIMESTAMP}
)

mapper.select:
SELECT s.*,u.nickname,u.level FROM yw_submission s LEFT JOIN yw_user u ON s.userid=u.id WHERE 1

    AND u.nickname LIKE concat('%',#{nickname,jdbcType=VARCHAR},'%')


    AND s.email=#{email.jdbcType=VARCHAR}


    AND s.status=#{type,jdbcType=INTEGER}


    AND u.level=#{vip,jdbcType=INTEGER}


    AND s.intime BETWEEN #{begintime,jdbcType=DATE} AND #{endtime,jdbcType=DATE}
