mapper.select:
select b.CNName,b.owner,b.id,b.certificate,b.webSite,b.publishTime,b.logourl,b.status,m.enterpriseName from t_p_brand b LEFT JOIN t_m_member m on b.createid = m.id WHERE 1=1
and b.status = #{status}
and b.CNName like CONCAT('%',#{CNName},'%')

SELECT DISTINCT b.id, b.CNName, p.scateid, b.logourl FROM t_p_product p LEFT JOIN t_p_brand b ON b.id = p.brandid WHERE b.status = 1 AND p.status = 1 and p.scateid IN (select id from t_p_category where parentId = #{parentId} and is_deleted = 0
id = #{subTypeId}
) ORDER BY b.views DESC
