mapper.select:
select courseid, publisherid, title, teacher, date_format(startTime,'%Y-%m-%d') startTime, date_format(endTime,'%Y-%m-%d') endTime, province, area, address, city, price, storageNumber, minBuyNumber, maxBuyNumber, date_format(expiryDate,'%Y-%m-%d') expiryDate, imgUrl, notice, courseType, status,notes from t_p_group_publishCourse
and courseid = #{courseid,jdbcType=BIGINT}
and courseType = #{courseType}

select t.courseid, t.publisherid, date_format(t.saleTime,'%Y-%m-%d') saleTime, t.title, t.teacher, date_format(t.startTime,'%Y-%m-%d') startTime,
concat(pv.name,'-',ct.name) provinceCity, t.price, t.minBuyNumber,
date_format(t.expiryDate,'%Y-%m-%d') expiryDate, t.status from t_p_group_publishCourse t
left join t_dictionary_province pv on pv.code = t.province
left join t_dictionary_city ct on ct.code = t.city


    and courseType = #{courseType}


    and status = #{status}


    and title like concat('%',#{title},'%')


    and province = #{province}


    and city = #{city}

select t.courseid,t.title,t.notes,
case when (locate(';',t.imgUrl) > 0) then substring(t.imgUrl,1,locate(';',t.imgUrl)-1) when locate(';',t.imgUrl) = 0 then imgUrl end as imgUrl
from t_p_group_publishCourse t


    and courseType = #{courseType}


    and status = #{status}


    and province = #{province}

select t.courseid,t.title,t.notes,
case when (locate(';',t.imgUrl) > 0) then substring(t.imgUrl,1,locate(';',t.imgUrl)-1) when locate(';',t.imgUrl) = 0 then imgUrl end as imgUrl
from t_p_group_publishCourse t


    and courseType = #{courseType}


    and status = #{status}


order by t.updateTime desc
limit #{count}
