mapper.select:
select courseid, publisherid, title, teacher, date_format(startTime,'%Y-%m-%d %H:%i:%s') startTime, date_format(endTime,'%Y-%m-%d %H:%i:%s') endTime, province, area, address, city, price, storageNumber, minBuyNumber, maxBuyNumber, date_format(expiryDate,'%Y-%m-%d') expiryDate, imgUrl, notice, courseType, status,notes from t_p_group_publishCourse
and courseid = #{courseid,jdbcType=BIGINT}
and courseType = #{courseType}

select t.courseid, t.publisherid, date_format(t.saleTime,'%Y-%m-%d') saleTime, t.title, t.teacher,
date_format(t.startTime,'%Y-%m-%d') startTime,
concat(pv.name,'-',ct.name) provinceCity, t.price, t.minBuyNumber,
date_format(t.expiryDate,'%Y-%m-%d') expiryDate, t.status from t_p_group_publishCourse t
left join t_dictionary_province pv on pv.code = t.province
left join t_dictionary_city ct on ct.code = t.city

    
        and courseType = #{courseType}
    
    
        and status = #{status}
    
    
        and title like concat('%',#{title},'%')
    
    
        and province = #{province}
    
    
        and city = #{city}
    

order by t.updateTime desc

select t.courseid,t.title,t.notes,date_format(t.endTime,'%Y-%m-%d %H:%i:%s') endTime, t.price,t.courseType, t.storageNumber, t.minBuyNumber, t.maxBuyNumber, ( select sum(number) from t_o_order_goods where goodsId = t.courseid and orderNo in (select orderNo from t_o_order where status in (1,2) and goodsType = t.courseType) ) as applynumber, case when (locate(';',t.imgUrl) > 0) then substring(t.imgUrl,1,locate(';',t.imgUrl)-1) when locate(';',t.imgUrl) = 0 then imgUrl end as imgUrl from t_p_group_publishCourse t
and courseType = #{courseType}
and status = #{status}
and province = #{province}
order by t.updateTime desc

select t.courseid,t.title,t.notes,( select sum(number) from t_o_order_goods where goodsId = t.courseid and orderNo in (select orderNo from t_o_order where status =2 and goodsType = t.courseType) ) as applynumber,t.status, (select sum(number) from t_o_order_goods where goodsId = t.courseid and orderNo in (select orderNo from t_o_order where status = 1 and goodsType = t.courseType and dealTime > date_add(now(),interval -30 minute))) as notBuyNumber, t.storageNumber,t.price,date_format(t.endTime,'%Y-%m-%d %H:%i:%s') endTime, case when (locate(';',t.imgUrl) > 0) then substring(t.imgUrl,1,locate(';',t.imgUrl)-1) when locate(';',t.imgUrl) = 0 then imgUrl end as imgUrl from t_p_group_publishCourse t 1=1
and t.courseType = #{courseType}
and t.status in(2,3)
and t.province = #{province}
AND t.expiryDate > now() AND t.storageNumber !=0
order by applynumber desc limit #{count}

select courseid, publisherid, title, teacher, date_format(startTime,'%Y-%m-%d %H:%i:%s') startTime, date_format(endTime,'%Y-%m-%d %H:%i:%s') endTime, concat(pv.name,ct.name,ar.name,address) address, price, minBuyNumber, maxBuyNumber,storageNumber, date_format(expiryDate,'%Y-%m-%d') expiryDate, imgUrl, notice, courseType, t.status,notes, (select sum(number) from t_o_order_goods where goodsId = #{courseid} and orderNo in (select orderNo from t_o_order where status = 1 and goodsType =#{courseType} and dealTime > date_add(now(),interval -30 minute))) as notBuyNumber, ( select sum(number) from t_o_order_goods where goodsId = #{courseid} and orderNo in (select orderNo from t_o_order where status = 2 and goodsType =#{courseType}) ) as number from t_p_group_publishCourse t left join t_dictionary_province pv on pv.code = t.province left join t_dictionary_city ct on ct.code = t.city left join t_dictionary_area ar on ar.code = t.area 1=1
and courseid = #{courseid,jdbcType=BIGINT}
and courseType = #{courseType}

select t.courseid,t.title,t.notes,t.expiryDate AS endTime,t.price,t.storageNumber,t.status, ( select sum(number) from t_o_order_goods where goodsId = t.courseid and orderNo in (select orderNo from t_o_order where status = 2 and goodsType= t.courseType )) AS applyNum , (select sum(number) from t_o_order_goods where goodsId = t.courseid and orderNo in (select orderNo from t_o_order where status = 1 and goodsType = t.courseType and dealTime > date_add(now(),interval -30 minute))) as notBuyNumber, case when (locate(';',t.imgUrl) > 0) then substring(t.imgUrl,1,locate(';',t.imgUrl)-1) when locate(';',t.imgUrl) = 0 then imgUrl end as imgUrl from t_p_group_publishCourse t where 1=1
and t.courseType= #{courseType}
and t.status != 1
and t.province = #{province}
order by t.expiryDate desc
