mapper.select:
select t.id, t.createID, t.title,dc.name systemCategory, dn.name applicationAreaName,t.applicationArea,
concat(po.name,'-',ct.name) provincecity,t.cooperation from t_tech_cooperation t
left join t_dictionary_province po on po.code = t.province
left join t_dictionary_city ct on ct.code = t.city
left join t_dictionary_area ar on ar.code = t.area
left join t_dictionary_constant dc on dc.code = t.systemCategory and dc.type =19
left join t_dictionary_constant dn on dn.code = t.applicationArea and dn.type =18
where t.status != 4

  and t.createID = #{createID}

   
      and t.status = #{status}
   
   
     and t.title like concat('%',#{title},'%')
   
   
   and t.systemCategory = #{systemCategory}
   
   
     and t.applicationArea = #{applicationArea}
   
   
     and t.type = #{type}
   
order by t.updateTime desc

select t.id, case when ur.mobile != '' or ur.mobile != null then ur.mobile when ur.email!= '' or ur.email != null then ur.email end as username, date_format(t.publishTime,'%Y-%m-%d') publishTime, date_format(t.updateTime,'%Y-%m-%d') updateTime, t.title, dc.name systemCategory, dn.name applicationArea, t.type, t.status from t_tech_cooperation t left join t_m_member ur on ur.id = t.createID left join t_dictionary_constant dc on dc.code = t.systemCategory and dc.type =19 left join t_dictionary_constant dn on dn.code = t.applicationArea and dn.type =18 where t.status != 4
and t.status = #{status}
and t.title like concat('%',#{title},'%')
and t.systemCategory = #{systemCategory}
and t.applicationArea = #{applicationArea}
and t.type = #{type}
order by t.updateTime desc

select id,createid,title, from t_tech_cooperation

  
    and status = #{status}
  

order by views desc limit #{count}

select t.id, date_format(t.publishTime,'%Y-%m-%d') publishTime, date_format(t.updateTime,'%Y-%m-%d') updateTime, t.title, dc.name systemCategory, dn.name applicationArea, t.linkman, t.companyName, t.telephone, t.email, po.name province,ct.name city,ar.name area, t.address, t.cooperation, t.notes,t.type from t_tech_cooperation t left join t_dictionary_province po on po.code = t.province left join t_dictionary_city ct on ct.code = t.city left join t_dictionary_area ar on ar.code = t.area left join t_dictionary_constant dc on dc.code = t.systemCategory and dc.type =19 left join t_dictionary_constant dn on dn.code = t.applicationArea and dn.type =18 where t.status != 4
and t.status = #{status}
and t.type = #{type}
order by t.type,t.updateTime desc limit #{count}
