mapper.select:
select pt.ticketId, pt.orderNo, gd.title, pt.mobile, pt.snCode, date_format(pt.operateTime,'%Y-%m-%d %H:%m:%s') operateTime, pt.status from t_o_pwdticket pt
left join t_p_group_publishCourse gd on gd.courseid = pt.courseId
where ticketId = #{ticketId,jdbcType=BIGINT}

select pt.ticketId, ur.username, pt.orderNo, gd.title, pt.mobile, pt.snCode, date_format(pt.operateTime,'%Y-%m-%d %H:%m:%s') operateTime, pt.status from t_o_pwdticket pt
left join t_p_group_publishCourse gd on gd.courseid = pt.courseId
left join t_oms_user ur on ur.id = pt.operateId


    and pt.snCode = #{snCode}


    and pt.ticketType = #{type}


    and pt.mobile = #{mobile}


    and pt.status = #{status}
