mapper.select:
select pt.ticketId, pt.orderNo, gd.goodsName, pt.mobile, pt.snCode, date_format(pt.operateTime,'%Y-%m-%d %H:%m:%s') operateTime, status from t_o_pwdticket pt
left join t_o_order_goods gd on gd.goodsId = pt.courseId
where ticketId = #{ticketId,jdbcType=BIGINT}

select pt.ticketId, ur.username, pt.orderNo, gd.goodsName, pt.mobile, pt.snCode, date_format(pt.operateTime,'%Y-%m-%d %H:%m:%s') operateTime, status from t_o_pwdticket pt
left join t_o_order_goods gd on gd.goodsId = pt.courseId
left join t_oms_user ur on ur.id = pt.operateId


    and snCode = #{snCode}


    and mobile = #{mobile}


    and status = #{status}
