mapper.select:
select

from yw_user
where id = #{id,jdbcType=INTEGER}

SELECT t.photo, t.level, t.nickname, t.sign,(
SELECT sum(collectsum) FROM yw_post WHERE userid = #{userid,jdbcType=INTEGER}
) AS becollectsum,
(
SELECT sum(forwardsum) FROM yw_post WHERE userid = #{userid,jdbcType=INTEGER}
) AS besharesum
FROM yw_user t WHERE t.id = #{userid,jdbcType=INTEGER}

SELECT id, photo, nickname, phone FROM yw_user WHERE id IN (
SELECT userid FROM yw_circle_manager WHERE circleid = #{circleid,jdbcType=INTEGER}
)

mapper.update:
UPDATE yw_user
SET points = points - #{points,jdbcType=INTEGER}
WHERE id = #{userid,jdbcType=INTEGER}

mapper.select:
select

from yw_user
where phone = #{phone}

mapper.update:
update yw_user
set
points =points+#{integral,jdbcType=INTEGER}
where phone = #{phone,jdbcType=VARCHAR}

update yw_user
set
points =points-#{integral,jdbcType=INTEGER}
where id = #{userid,jdbcType=INTEGER}

mapper.select:
SELECT u.points
FROM yw_user u
WHERE u.id=#{id,jdbcType=INTEGER}

SELECT count(1) FROM yw_user appuser LEFT JOIN yw_admin_user bossuser
ON appuser.phone = bossuser.phone
WHERE appuser.phone = #{phone}

mapper.update:
update yw_user
SET token = #{token}
WHERE phone = #{phone}

mapper.select:
SELECT u.id,u.nickname,u.phone,u.level,u.status, (SELECT COUNT(1) FROM yw_post p WHERE p.userid=u.id) AS postsum, (SELECT COUNT(1) FROM yw_post po WHERE po.userid=u.id and po.isessence=1) AS isessencesum, (SELECT COALESCE(SUM(points),0) FROM yw_post_rewarded r WHERE r.postid IN (SELECT p1.id FROM yw_post p1 WHERE p1.userid=vd.userid AND p1.isdel=0)) AS exceptional, (SELECT COALESCE(SUM(collectsum),0) FROM yw_post p2 WHERE p2.userid=vd.userid AND p2.isdel=0) AS becollectsum, (SELECT COUNT(1) FROM yw_post_share s WHERE s.postid IN (SELECT p5.id FROM yw_post p5 WHERE p5.userid=vd.userid AND p5.isdel=0)) AS besharesum, (SELECT COALESCE(SUM(commentsum),0) FROM yw_post p3 WHERE p3.userid=vd.userid AND p3.isdel=0) AS commentsum, (SELECT COALESCE(SUM(zansum),0) FROM yw_post p4 WHERE p4.userid=vd.userid AND p4.isdel=0) AS zansum,u.fans,u.applydate FROM yw_user u LEFT JOIN yw_apply_vip_detail vd ON vd.userid=u.id WHERE vd.userid=u.id
AND u.nickname LIKE concat('%',#{username,jdbcType=VARCHAR},'%')
AND u.phone=#{phone,jdbcType=VARCHAR}
AND vd.apply_time BETWEEN #{begintime,jdbcType=DATE} AND #{endtime,jdbcType=DATE}

SELECT u.id,u.nickname,u.phone,u.level,u.status,u.points,u.fans,u.intime,u.qq,u.sina,u.openid,aut.authstatus,
(SELECT COUNT(1) FROM yw_coupon WHERE isdel=0 AND userid=u.id) AS coupon,
(SELECT COUNT(1) FROM yw_post WHERE isdel=0 AND userid=u.id) AS postsum,
(SELECT COUNT(1) FROM yw_post WHERE isdel=0 AND userid=u.id AND isessence=1) AS isessencesum
FROM yw_user u LEFT JOIN yw_autonym_authentication aut ON aut.userid=u.id WHERE 1


    AND u.nickname LIKE concat('%',#{nickname,jdbcType=VARCHAR},'%')


    AND u.phone=#{phone,jdbcType=VARCHAR}


    AND authstatus=#{authentication,jdbcType=INTEGER}


    AND level=#{level,jdbcType=INTEGER}


    AND status=#{status,jdbcType=INTEGER}


    AND intime BETWEEN #{begintime,jdbcType=DATE} AND #{endtime,jdbcType=DATE}




            AND u.points > #{price,jdbcType=INTEGER}


            AND u.points = #{price,jdbcType=INTEGER}


            AND u.points < #{price,jdbcType=INTEGER}




            AND coupon > #{price,jdbcType=INTEGER}


            AND coupon=#{price,jdbcType=INTEGER}


            AND coupon < #{price,jdbcType=INTEGER}




            AND postsum > #{price,jdbcType=INTEGER}


            AND postsum=#{price,jdbcType=INTEGER}


            AND postsum < #{price,jdbcType=INTEGER}




            AND isessencesum > #{price,jdbcType=INTEGER}


            AND isessencesum=#{price,jdbcType=INTEGER}


            AND isessencesum < #{price,jdbcType=INTEGER}




            AND u.fans > #{price,jdbcType=INTEGER}


            AND u.fans = #{price,jdbcType=INTEGER}


            AND u.fans < #{price,jdbcType=INTEGER}





        AND u.phone <> ''


        AND u.openid <> ''


        AND u.qq <> ''


        AND u.sina <> ''




        ORDER BY coupon ASC


        ORDER BY coupon DESC




        ORDER BY postsum ASC


        ORDER BY postsum DESC




        ORDER BY isessencesum ASC


        ORDER BY isessencesum DESC




        ORDER BY fans ASC


        ORDER BY fans DESC

SELECT u.id,u.nickname,u.phone,u.level,u.status,u.points,u.fans,u.intime,u.qq,u.sina,u.openid,aut.authstatus, (SELECT COUNT(1) FROM yw_coupon WHERE isdel=0 AND userid=u.id) AS coupon, (SELECT COUNT(1) FROM yw_post WHERE isdel=0 AND userid=u.id) AS postsum, (SELECT COUNT(1) FROM yw_post WHERE isdel=0 AND userid=u.id AND isessence=1) AS isessencesum FROM yw_user u LEFT JOIN yw_autonym_authentication aut ON aut.userid=u.id WHERE u.id IN (SELECT userid FROM yw_follow_circle fc WHERE fc.circleid=#{circleid,jdbcType=INTEGER}
AND date(fc.intime) = curdate()
)
