mapper.select:
SELECT u.id,u.nickname,u.phone,u.level,u.status, (SELECT COUNT(1) FROM yw_post p WHERE p.userid=u.id) AS postsum, (SELECT COUNT(1) FROM yw_post po WHERE po.userid=u.id and po.isessence=1) AS isessencesum, (SELECT COALESCE(SUM(points),0) FROM yw_post_rewarded r WHERE r.postid IN (SELECT p1.id FROM yw_post p1 WHERE p1.userid=u.id AND p1.isdel=0)) AS exceptional, (SELECT COALESCE(SUM(collectsum),0) FROM yw_post p2 WHERE p2.userid=u.id AND p2.isdel=0) AS becollectsum, (SELECT COUNT(1) FROM yw_post_share s WHERE s.postid IN (SELECT p5.id FROM yw_post p5 WHERE p5.userid=u.id AND p5.isdel=0)) AS besharesum, (SELECT COALESCE(SUM(commentsum),0) FROM yw_post p3 WHERE p3.userid=u.id AND p3.isdel=0) AS commentsum, (SELECT COALESCE(SUM(zansum),0) FROM yw_post p4 WHERE p4.userid=u.id AND p4.isdel=0) AS zansum,u.fans,vd.apply_time AS applydate,au.authstatus FROM (yw_user u LEFT JOIN yw_apply_vip_detail vd ON vd.userid=u.id) LEFT JOIN yw_autonym_authentication au ON au.userid=u.id WHERE u.level>0 AND u.status=0
AND u.nickname LIKE concat('%',#{name,jdbcType=VARCHAR},'%')
AND u.phone=#{phone,jdbcType=VARCHAR}
AND vd.apply_time BETWEEN #{begintime,jdbcType=DATE} AND #{endtime,jdbcType=DATE}
AND au.authstatus=#{authstatus,jdbcType=INTEGER}
AND au.authstatus=#{authstatus,jdbcType=INTEGER}
AND au.authstatus=#{authstatus,jdbcType=INTEGER}
AND au.authstatus IS NULL
ORDER BY vd.apply_time DESC
ORDER BY level DESC
ORDER BY intime

SELECT u.id,u.nickname,u.phone,u.level,u.status,vd.id AS appyid, (SELECT authstatus FROM yw_autonym_authentication WHERE userid=u.id) AS authstatus, (SELECT COUNT(1) FROM yw_post p WHERE p.userid=u.id) AS postsum, (SELECT COUNT(1) FROM yw_post po WHERE po.userid=u.id and po.isessence=1) AS isessencesum, (SELECT COALESCE(SUM(points),0) FROM yw_post_rewarded r WHERE r.postid IN (SELECT p1.id FROM yw_post p1 WHERE p1.userid=vd.userid AND p1.isdel=0)) AS exceptional, (SELECT COALESCE(SUM(collectsum),0) FROM yw_post p2 WHERE p2.userid=vd.userid AND p2.isdel=0) AS becollectsum, (SELECT COUNT(1) FROM yw_post_share s WHERE s.postid IN (SELECT p5.id FROM yw_post p5 WHERE p5.userid=vd.userid AND p5.isdel=0)) AS besharesum, (SELECT COALESCE(SUM(commentsum),0) FROM yw_post p3 WHERE p3.userid=vd.userid AND p3.isdel=0) AS commentsum, (SELECT COALESCE(SUM(zansum),0) FROM yw_post p4 WHERE p4.userid=vd.userid AND p4.isdel=0) AS zansum,u.fans,u.applydate FROM yw_user u LEFT JOIN yw_apply_vip_detail vd ON vd.userid=u.id LEFT JOIN yw_autonym_authentication au ON au.userid=u.id WHERE vd.userid=u.id AND vd.isdel=0 AND u.status=0 AND u.phone IS NOT NULL
AND u.nickname LIKE concat('%',#{username,jdbcType=VARCHAR},'%')
AND u.phone=#{phone,jdbcType=VARCHAR}
AND (SELECT authstatus FROM yw_autonym_authentication WHERE userid=u.id)=#{authstatus,jdbcType=INTEGER}
AND (SELECT authstatus FROM yw_autonym_authentication WHERE userid=u.id)=#{authstatus,jdbcType=INTEGER}
AND (SELECT authstatus FROM yw_autonym_authentication WHERE userid=u.id)=#{authstatus,jdbcType=INTEGER}
AND (SELECT authstatus FROM yw_autonym_authentication WHERE userid=u.id) IS NULL
AND vd.apply_time BETWEEN #{begintime,jdbcType=DATE} AND #{endtime,jdbcType=DATE}
ORDER BY vd.apply_time DESC
ORDER BY u.level DESC

SELECT u.id,u.nickname,u.phone,u.level,u.status,vd.id AS appyid, (SELECT authstatus FROM yw_autonym_authentication WHERE userid=u.id) AS autonym, (SELECT COUNT(1) FROM yw_post p WHERE p.userid=u.id) AS postsum, (SELECT COUNT(1) FROM yw_post po WHERE po.userid=u.id and po.isessence=1) AS isessencesum, (SELECT COALESCE(SUM(points),0) FROM yw_post_rewarded r WHERE r.postid IN (SELECT p1.id FROM yw_post p1 WHERE p1.userid=vd.userid AND p1.isdel=0)) AS exceptional, (SELECT COALESCE(SUM(collectsum),0) FROM yw_post p2 WHERE p2.userid=vd.userid AND p2.isdel=0) AS becollectsum, (SELECT COUNT(1) FROM yw_post_share s WHERE s.postid IN (SELECT p5.id FROM yw_post p5 WHERE p5.userid=vd.userid AND p5.isdel=0)) AS besharesum, (SELECT COALESCE(SUM(commentsum),0) FROM yw_post p3 WHERE p3.userid=vd.userid AND p3.isdel=0) AS commentsum, (SELECT COALESCE(SUM(zansum),0) FROM yw_post p4 WHERE p4.userid=vd.userid AND p4.isdel=0) AS zansum,u.fans,u.applydate FROM yw_user u LEFT JOIN yw_audit_vip_detail vd ON vd.apply_userid=u.id LEFT JOIN yw_autonym_authentication au ON au.userid=u.id WHERE vd.apply_userid=u.id AND u.status=0 AND u.phone IS NOT NULL
AND u.nickname LIKE concat('%',#{username,jdbcType=VARCHAR},'%')
AND u.phone=#{phone,jdbcType=VARCHAR}
AND (SELECT authstatus FROM yw_autonym_authentication WHERE userid=u.id)=#{authstatus,jdbcType=INTEGER}
AND (SELECT authstatus FROM yw_autonym_authentication WHERE userid=u.id)=#{authstatus,jdbcType=INTEGER}
AND (SELECT authstatus FROM yw_autonym_authentication WHERE userid=u.id)=#{authstatus,jdbcType=INTEGER}
AND (SELECT authstatus FROM yw_autonym_authentication WHERE userid=u.id) IS NULL
AND vd.apply_time BETWEEN #{begintime,jdbcType=DATE} AND #{endtime,jdbcType=DATE}
ORDER BY vd.apply_time DESC
ORDER BY u.level DESC

SELECT u.id,u.nickname,u.phone,u.level,u.status,u.points,u.fans,u.intime,u.qq,u.sina,u.openid,aut.authstatus,
(SELECT COUNT(1) FROM yw_coupon WHERE isdel=0 AND userid=u.id) AS coupon,
(SELECT COUNT(1) FROM yw_post WHERE isdel=0 AND userid=u.id AND isactive=0) AS postsum,
(SELECT COUNT(1) FROM yw_post WHERE isdel=0 AND userid=u.id AND isessence=1 AND isactive=0) AS isessencesum
FROM yw_user u LEFT JOIN yw_autonym_authentication aut ON aut.userid=u.id WHERE 1 AND u.phone IS NOT NULL

    AND u.nickname LIKE concat('%',#{nickname,jdbcType=VARCHAR},'%')


    AND u.phone=#{phone,jdbcType=VARCHAR}


    AND aut.authstatus=#{authentication,jdbcType=INTEGER}


    AND aut.authstatus=#{authentication,jdbcType=INTEGER}


    AND aut.authstatus=#{authentication,jdbcType=INTEGER}


    AND aut.authstatus IS NULL


    AND level=#{level,jdbcType=INTEGER}


    AND u.status=#{status,jdbcType=INTEGER}


    AND u.intime BETWEEN #{begintime,jdbcType=DATE} AND #{endtime,jdbcType=DATE}




            AND u.points > #{price,jdbcType=INTEGER}


            AND u.points = #{price,jdbcType=INTEGER}


            AND u.points < #{price,jdbcType=INTEGER}




            AND coupon > #{price,jdbcType=INTEGER}


            AND coupon=#{price,jdbcType=INTEGER}


            AND coupon < #{price,jdbcType=INTEGER}




            AND postsum > #{price,jdbcType=INTEGER}


            AND postsum=#{price,jdbcType=INTEGER}


            AND postsum < #{price,jdbcType=INTEGER}




            AND isessencesum > #{price,jdbcType=INTEGER}


            AND isessencesum=#{price,jdbcType=INTEGER}


            AND isessencesum < #{price,jdbcType=INTEGER}




            AND u.fans > #{price,jdbcType=INTEGER}


            AND u.fans = #{price,jdbcType=INTEGER}


            AND u.fans < #{price,jdbcType=INTEGER}





        AND u.phone IS NOT NULL


        AND u.openid IS NOT NULL


        AND u.qq IS NOT NULL


        AND u.sina IS NOT NULL




        ORDER BY coupon ASC


        ORDER BY coupon DESC




        ORDER BY postsum ASC


        ORDER BY postsum DESC




        ORDER BY isessencesum ASC


        ORDER BY isessencesum DESC




        ORDER BY fans ASC


        ORDER BY fans DESC




        ORDER BY u.intime ASC


        ORDER BY u.intime DESC


        ORDER BY u.level ASC

SELECT u.id,u.nickname,u.phone,u.level,u.status,u.points,u.fans,u.intime,u.qq,u.sina,u.openid,aut.authstatus, (SELECT COUNT(1) FROM yw_coupon WHERE isdel=0 AND userid=u.id) AS coupon, (SELECT COUNT(1) FROM yw_post WHERE isdel=0 AND userid=u.id) AS postsum, (SELECT COUNT(1) FROM yw_post WHERE isdel=0 AND userid=u.id AND isessence=1) AS isessencesum FROM yw_user u LEFT JOIN yw_autonym_authentication aut ON aut.userid=u.id WHERE u.id IN (SELECT userid FROM yw_follow_circle fc WHERE fc.circleid=#{circleid,jdbcType=INTEGER} AND u.status=0 AND u.phone IS NOT NULL
AND date(fc.intime) = curdate()
)

