mapper.select:
  	SELECT 
	  id,member_id,type,pinyin,name,value,description,add_time,update_time 
	FROM
	  t_vip_member_privilege 
	WHERE member_id = #{memberId,jdbcType=BIGINT} 
	  AND pinyin = #{pinyin,jdbcType=VARCHAR}

SELECT o.buyerId AS id, date_format(o.dealTime,'%Y-%m-%d %H:%i:%s') AS buyTime, CASE WHEN m.mobile IS NOT NULL AND m.mobile != '' THEN m.mobile WHEN m.email IS NOT NULL AND m.email != '' THEN m.email END AS account, m.enterpriseName AS NAME, g.goodsId, g.goodsName AS vipLevel, 1 AS activeTime, o.payAmount price, v.vip_level FROM t_o_order o LEFT JOIN t_m_member m ON o.buyerId = m.id LEFT JOIN t_o_order_goods g ON o.orderNo = g.orderNo LEFT JOIN t_vip_member_info v ON o.buyerId = v.member_id WHERE o.goodsType = '3' AND NOT EXISTS (SELECT 1 FROM t_o_order o1 WHERE o1.buyerId = o.buyerId AND o1.dealTime > o.dealTime)
AND (m.mobile = #{account,jdbcType=VARCHAR} OR m.email = #{account,jdbcType=VARCHAR})
AND m.enterpriseName = #{name,jdbcType=VARCHAR}
AND g.goodsId = #{vipLevel,jdbcType=BIGINT}
AND (v.vip_level IN (30,60,130,160))
AND (v.vip_level IN (0,100))
